//Reference: https://jsfiddle.net/fwv18zo1/
function populate(s1,s2)
{
   var s1 = document.getElementById(s1)
   var s2 = document.getElementById(s2)

   s2.innerHTML = "";
   if(s1.value =="mathsbiology")
   {
       var optionArray= ['bams|BAMS','bds|BDS','bhms|BHMS'];
   }
   if(s1.value =="computerscience")
   {
          var optionArray= ['bsccomputerscience|Bsc.Computerscience','btech|Btech','bca|BCA'];

   }
   if(s1.value =="commerce")
   {
          var optionArray= ['bba|BBA','bcom|BCom','digitalmarketing|Digital Marketing'];

   }
   if(s1.value =="humanities")
   {
          var optionArray= ['basociology|BA.Sociology','bapsychology|BA.Psychology','llb|LLB'];

   }
   if(s1.value =="electronics")
   {
          var optionArray= ['eee|EEE','ece|ECE','bscelectronics|Bsc.Electronics'];

   }
   if(s1.value =="statstics")
   {
          var optionArray= ['bscstatstics|Bsc.Statstics'];

   }
   for(var option in optionArray)
   {
         var pair =optionArray[option].split("|");
         var newoption =document.createElement("option");

         newoption.value = pair[0];
         newoption.innerHTML=pair[1];
         s2.options.add(newoption);
   }
}


